// Get all input items (array of {json: {...}}) from previous node
const inputItems = $input.all();

// Extract the JSON objects from input
const inputData = inputItems.map(item => item.json);

const extractProductSummary = (products) => {
  return products.map(product => ({
    Sno: product.Sno,
    ProductName: product.ProductName ? product.ProductName.trim() : "",
    category: product.category || ""
  }));
};

// Get simplified data
const output = extractProductSummary(inputData);

// n8n requires array of { json: ... } objects to pass output
return output.map(item => ({ json: item }));

